media_type.script:
var Telegram = {

    token: null,
    to: null,
    message: null,

    url_bot_general: 'https://api.telegram.org/bot',
    disable_web_page_preview: true,
    disable_notification: false,
    parse_mode: null,

    request: new CurlHttpRequest(),

    sendMessage: function(method) {

        var params = {
                        sendMessage: {
                            chat_id: this.to,
                            text: this.message,
                            disable_web_page_preview: this.disable_web_page_preview,
                            disable_notification: this.disable_notification
                        }
                     };

        var url = this.url_bot_general + this.token + '/' + method;

        if (this.parse_mode !== null) {
            params[method]['parse_mode'] = this.parse_mode;
        }

        this.request.AddHeader('Content-Type: application/json');

        Zabbix.Log(4, '[Telegram Webhook] params: ' + JSON.stringify(params[method]));
        Zabbix.Log(4, '[Telegram Webhook] URL: ' + url.replace(this.token, 'XXX'));  // remove replace() function if you want to see the exposed token in the log file
        var response = this.request.Post(url, JSON.stringify(params[method]));
        Zabbix.Log(4, '[Telegram Webhook] HTTP code: ' + this.request.Status());
        if (this.request.Status() != 200) {
            throw response;
        }

        return response;

    }
}

try {

    parameters = JSON.parse(value);

    Zabbix.Log(4, '[Telegram Webhook] script value= ' + value);

    if (typeof parameters.telegramToken === 'undefined') {
        Zabbix.Log(4, '[Telegram Webhook] Incorrect value is given for parameter "telegramToken": parameter is missing');
        throw 'Incorrect value is given for parameter "telegramToken": parameter is missing';
    }

    Telegram.token = parameters.telegramToken;

    if ((typeof parameters.telegramParseMode !== 'undefined') && (['Markdown', 'HTML', 'MarkdownV2'].indexOf(parameters.telegramParseMode) !== -1)) {
        Telegram.parse_mode = parameters.telegramParseMode;
    }

    Telegram.to = parameters.To;
    Telegram.message = parameters.Subject + '\n' + parameters.Message;
    var send_message_result = Telegram.sendMessage('sendMessage');

    return send_message_result;

} catch (error) {
    Zabbix.Log(4, '[Telegram Webhook] notification failed: ' + error);
    throw '[Telegram Webhook] notification failed: ' + error;
}
